package org.example.cli.puzzle6;

import java.io.IOException;
import java.math.BigInteger;
import java.nio.file.Files;
import java.nio.file.Path;
import java.util.ArrayList;
import java.util.List;

public class Puzzle6 {

    public static void main(String[] args) {
        Path inputPath = Path.of("src/main/java/org/example/cli/puzzle6/6.input");
        try {
            List<String> lines = Files.readAllLines(inputPath);
            if (lines.isEmpty()) {
                System.out.println("Input file is empty.");
                return;
            }

            // Remove trailing empty lines if any, but keep the operator line which should be the last non-empty line
            while (!lines.isEmpty() && lines.getLast().isBlank()) {
                lines.removeLast();
            }

            if (lines.isEmpty()) {
                System.out.println("Input file contains only whitespace.");
                return;
            }

            // Calculate max width
            int maxWidth = 0;
            for (String line : lines) {
                maxWidth = Math.max(maxWidth, line.length());
            }

            // Identify empty columns
            boolean[] isEmptyColumn = new boolean[maxWidth];
            for (int x = 0; x < maxWidth; x++) {
                boolean allSpaces = true;
                for (String line : lines) {
                    if (x < line.length() && line.charAt(x) != ' ') {
                        allSpaces = false;
                        break;
                    }
                }
                isEmptyColumn[x] = allSpaces;
            }

            BigInteger grandTotal = BigInteger.ZERO;
            int x = 0;
            while (x < maxWidth) {
                if (!isEmptyColumn[x]) {
                    // Start of a problem block
                    int start = x;
                    while (x < maxWidth && !isEmptyColumn[x]) {
                        x++;
                    }
                    int end = x; // Exclusive

                    // Process block [start, end)
                    processBlock(lines, start, end, grandTotal);

                    // grandTotal is effectively final in lambda, so we pass a mutable container or just add return value
                    // Actually, let's change processBlock to return BigInteger
                } else {
                    x++;
                }
            }

            // Refactoring loop to sum up results
            grandTotal = BigInteger.ZERO;
            x = 0;
            while (x < maxWidth) {
                if (!isEmptyColumn[x]) {
                    int start = x;
                    while (x < maxWidth && !isEmptyColumn[x]) {
                        x++;
                    }
                    int end = x;
                    grandTotal = grandTotal.add(solveBlock(lines, start, end));
                } else {
                    x++;
                }
            }

            System.out.println("Grand Total: " + grandTotal);

        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private static BigInteger solveBlock(List<String> lines, int start, int end) {
        List<BigInteger> numbers = new ArrayList<>();
        String operator = null;

        // The operator is in the last line
        String lastLine = lines.getLast();
        if (start < lastLine.length()) {
            String opPart = lastLine.substring(start, Math.min(end, lastLine.length())).trim();
            if (!opPart.isEmpty()) {
                operator = opPart;
            }
        }

        // Numbers are in the lines before the last one
        for (int i = 0; i < lines.size() - 1; i++) {
            String line = lines.get(i);
            if (start < line.length()) {
                String numPart = line.substring(start, Math.min(end, line.length())).trim();
                if (!numPart.isEmpty()) {
                    try {
                        numbers.add(new BigInteger(numPart));
                    } catch (NumberFormatException e) {
                        System.err.println("Invalid number: '" + numPart + "' in block " + start + "-" + end);
                    }
                }
            }
        }

        if (operator == null) {
             System.err.println("No operator found for block " + start + "-" + end);
             return BigInteger.ZERO;
        }

        BigInteger result;
        if (operator.equals("+")) {
            result = BigInteger.ZERO;
            for (BigInteger n : numbers) {
                result = result.add(n);
            }
        } else if (operator.equals("*")) {
            result = BigInteger.ONE;
            for (BigInteger n : numbers) {
                result = result.multiply(n);
            }
        } else {
             System.err.println("Unknown operator: '" + operator + "' in block " + start + "-" + end);
             return BigInteger.ZERO;
        }

        return result;
    }
}

